<?php global $post, $smof_data, $adults, $children; ?><div id="booking-form" class="white-popup-block mfp-hide"><div id="result">&nbsp;</div><form class="send-tour-booking" action="" method="post">	<h4><?php echo __('Submit tour booking', SP_TEXT_DOMAIN); ?></h4>	<h6><?php echo __('Personal information', SP_TEXT_DOMAIN); ?></h6>	<div class="one-fourth">	<label for="title"><?php echo __('Title', SP_TEXT_DOMAIN); ?></label>	<select id="title" name="title">		<option value="1">Mr.</option>		<option value="2">Ms.</option>	</select>	</div> 	<div class="three-fourth last">	<label for="full_name"><?php echo __('Full name', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="full_name" name="full_name" />	</div>	<div class="clear"></div>	<div class="two-fourth">	<label for="email"><?php echo __('Email address', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="email" name="email" />	</div>	<div class="two-fourth last">	<label for="confirm_email"><?php echo __('Confirm email address', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="confirm_email" name="confirm_email" />	</div>	<div class="clear"></div>	<div class="one-fourth">	<label for="phone_number"><?php echo __('Phone number', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="phone_number" name="phone_number" />	</div>	<div class="one-fourth">	<label for="country"><?php echo __('Country', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="country" name="country" />	</div>	<div class="one-fourth">	<label for="address"><?php echo __('Address', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="address" name="address" />	</div>	<div class="one-fourth last">	<label for="town"><?php echo __('Town / City', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="town" name="town">	</div>	<div class="clear"></div>	<h6><?php echo __('Tour information', SP_TEXT_DOMAIN); ?></h6>	<?php $duration = get_post_meta( get_the_ID(), 'sp_day', true ); ?>	<p><span class="tour-selected"><?php the_title(); ?></span> <br>	<?php echo sprintf( esc_attr__( 'Destination:  %1$s', SP_TEXT_DOMAIN ),			 sp_get_tour_destination()); ?> <br>	<?php echo sprintf( esc_attr__( 'Duration:  %1$s days / %2$s night', SP_TEXT_DOMAIN ),			 $duration + 1,			 $duration); ?></p>	<div class="one-fourth">	<label for="accommodation"><?php echo __('Accommodation', SP_TEXT_DOMAIN); ?></label>	<select id="accommodation" name="accommodation">		<option value="1"><?php _e('Hotel 3 star');?></option>		<option value="2"><?php _e('Hotel 4 star');?></option>		<option value="3"><?php _e('Hotel 5 star');?></option>	</select>	</div>	<div class="one-fourth">	<label for="arrive_date"><?php echo __('Arrive date', SP_TEXT_DOMAIN); ?></label>	<input type="text" id="arrive_date" name="arrive_date" />	</div>		<div class="one-fourth">	<label for="adults"><?php echo __('Adults', SP_TEXT_DOMAIN); ?></label>	<select id="adults" name="adults">	<?php foreach( $adults as $adult){ ?>		<option value="<?php echo $adult; ?>"><?php echo $adult; ?></option>	<?php } ?>	</select>	</div>		<div class="one-fourth last">	<label for="children"><?php echo __('Children', SP_TEXT_DOMAIN); ?></label>	<select id="children" name="children">	<?php foreach( $children as $child){ ?>		<option value="<?php echo $child; ?>"><?php echo $child; ?></option>	<?php } ?>	</select>	</div>	<div class="clear"></div>	<label for="requirements"><?php echo __('Special requirements: (Not Guaranteed)', SP_TEXT_DOMAIN); ?></label>	<textarea rows="5" name="requirements" id="requirements"></textarea>		<p><input type="submit" value="<?php echo __('Submit Booking', SP_TEXT_DOMAIN); ?>" /></p>	<input type="hidden" name="tour_name" value="<?php the_title(); ?>">	<input type="hidden" name="destination" value="<?php echo sp_get_tour_destination(); ?>"></form></div> <!-- #booking-form -->