<?php $term = get_term_by( 'slug', get_query_var( 'term' ), get_query_var( 'taxonomy' ) );if ( is_tax() && $term->taxonomy == 'faq-category' ) : ?><article id="faq-archive"><ul><?php while ( have_posts() ) : the_post(); ?>		<li><a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', SP_TEXT_DOMAIN ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a></li><?php endwhile; ?>	</ul>	</section><?php else: ?><article id="post-<?php the_ID(); ?>" <?php post_class('clearfix'); ?>><ul>	<?php while ( have_posts() ) : the_post(); ?>		<li><a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', SP_TEXT_DOMAIN ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a><?php the_excerpt(); ?></li><?php endwhile; ?>	</ul>	</article>	<?php endif; ?>