<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<header class="entry-header">			<h1 class="entry-title"><?php the_title(); ?></h1>		</header>		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->	</article><!-- #post -->	<div id="related-fqas">		<h3><?php _e( 'Related FAQs:', SP_TEXT_DOMAIN ); ?></h3>		<ul>		<?php $taxonomy = 'faq-category';			$post_related = sp_get_posts_related_by_taxonomy( $post->ID, $taxonomy );			while ( $post_related->have_posts() ): $post_related->the_post(); ?>			<li><a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', SP_TEXT_DOMAIN ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a></li>		<?php endwhile; ?>		</ul>	</div><?php endwhile;else : ?>		<?php get_template_part('library/content/error404'); ?>	<?php endif; ?>