<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<article id="post-<?php the_ID(); ?>" <?php post_class(); ?>>		<header class="entry-header">			<h1 class="entry-title">			<?php 				if (is_single()) { 					the_title(); 				} else { 			?>			<a href="<?php the_permalink(); ?>" title="<?php the_title(); ?>"><?php the_title(); ?></a>			<?php } ?>			</h1>			<div class="entry-meta"><?php sp_post_meta(); ?></div>		</header>		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->	</article><!-- #post --><?php endwhile;else : ?>		<?php get_template_part('library/content/error404'); ?>	<?php endif; ?>