<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<?php $tours_day = get_post_meta( $post->ID, 'sp_day', true ); 		$duration = get_post_meta( $post->ID, 'sp_duration', true ); 		$departure = get_post_meta( $post->ID, 'sp_departure', true ); 		$overview = get_post_meta( $post->ID, 'sp_overview', true ); 		$tour_type = wp_get_post_terms( $post->ID, 'tour-type' );		$destinations = wp_get_post_terms( $post->ID, 'destination' );	?>	<article id="post-<?php the_ID(); ?>">		<header class="entry-header">			<h1 class="entry-title">			<?php 				if (is_single()) { 					the_title(); 				} else { 			?>			<a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', SP_TEXT_DOMAIN ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a>			<?php } ?>			</h1>		</header>		<ul class="tour-meta">			<li>			<span class="meta-label"><?php echo esc_attr__( 'Duration: ', SP_TEXT_DOMAIN ); ?></span>			<span class="meta-value">			<?php printf( esc_attr__( '%1$s days / %2$s night', SP_TEXT_DOMAIN ),			 $tours_day,			 $tours_day - 1); ?>			 </span>			 </li>			 <li>			 <span class="meta-label"><?php echo esc_attr__( 'Destination: ', SP_TEXT_DOMAIN ); ?></span>			 <span class="meta-value">			 <?php 			 $out = '';			 foreach ($destinations as $destination) {				$out .= $destination->name . ', ';			 }			 echo rtrim($out, ', ');			 ?>			 </span>				 </li>			 <li>			 <span class="meta-label"><?php echo esc_attr__( 'Departure: ', SP_TEXT_DOMAIN ); ?></span>			 <span class="meta-value"><?php echo esc_attr__( $departure ); ?></span>				 </li>			 <li>			 <span class="meta-label"><?php echo esc_attr__( 'Type: ', SP_TEXT_DOMAIN ); ?></span>			 <span class="meta-value">			 <?php 			 $out = '';			 foreach ($tour_type as $type) {				$out .= $type->name . ', ';			 }			 echo rtrim($out, ', ');			 ?>			 </span>				 </li>			 <li>			 <?php echo $overview; ?>				 </li>		</ul> <!-- .tour-meta -->		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->			</article>	<?php endwhile;else : ?>		<?php get_template_part('library/content/error404'); ?>	<?php endif; ?>