<?php if ( have_posts() ) :	while ( have_posts() ) :	the_post(); ?>	<article id="post-<?php the_ID(); ?>">		<header class="entry-header">			<h1 class="entry-title">			<?php 				if (is_single()) { 					the_title(); 				} else { 			?>			<a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', SP_TEXT_DOMAIN ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a>			<?php } ?>			</h1>		</header>		<div class="tour-meta">			<?php echo sp_tour_meta(); ?>		</div> <!-- .tour-meta -->		<div class="entry-content">			<?php the_content(); ?>		</div><!-- .entry-content -->		<div class="tour-photos">			<h3><?php _e( 'Tour photos', SP_TEXT_DOMAIN ); ?></h3>			<?php echo sp_tour_photos(); ?>		</div> <!-- .tour-photos -->		<h3><?php _e( 'What is included', SP_TEXT_DOMAIN ); ?></h3>		<?php echo sp_price_included_exlcuded(); ?>		<div id="related-tours">			<h3><?php _e( 'Related Trous:', SP_TEXT_DOMAIN ); ?></h3>			<ul>			<?php $taxonomy = 'destination';				$post_related = sp_get_posts_related_by_taxonomy( $post->ID, $taxonomy );				while ( $post_related->have_posts() ): $post_related->the_post(); ?>				<li><a href="<?php the_permalink(); ?>" title="<?php printf( esc_attr__( 'Permalink to %s', SP_TEXT_DOMAIN ), the_title_attribute( 'echo=0' ) ); ?>" rel="bookmark"><?php the_title(); ?></a></li>			<?php endwhile; ?>			</ul>		</div>			</article>	<?php endwhile;else : ?>		<?php get_template_part('library/content/error404'); ?>	<?php endif; ?>