<?php global $smof_data;$custom_search_page = get_page_by_title($smof_data['search_tour_page']);//allows you to modify the search parameters. for example bbpress search_id needs to be 'bbp_search' instead of 'term'. you can also deactivate ajax search by setting ajax_disable to true$search_params_tour = array(		'placeholder'  	=> __('Where do you want to go', SP_TEXT_DOMAIN),	'search_id'	   	=> 'term',	'form_action'	=> get_page_link($custom_search_page->ID),	'submit'		=> __('Search', SP_TEXT_DOMAIN));?><form action="<?php echo $search_params_tour['form_action']; ?>" id="searchform-tour" method="get">	<div>		<input type="text" id="<?php echo $search_params_tour['search_id']; ?>" name="<?php echo $search_params_tour['search_id']; ?>" value="<?php if(!empty($_GET['term'])) echo get_search_query(); ?>" placeholder='<?php echo $search_params_tour['placeholder']; ?>' />		<?php 			$des_args = array(				'hierarchical' => 1,				'hide_empty' => 0, 				'taxonomy' => 'destination', 				'orderby' => 'name',				'id' => 'destination',				'name' => 'destination',				'class' => 'destination',				'depth' => 2			);			wp_dropdown_categories($des_args); 		?>		<?php			$type_args = array(				'taxonomy' => 'tour-type', 				'hide_empty' => 0,				'orderby' => 'name',				'id' => 'tour_type',				'name' => 'tour_type',				'class' => 'tour_type'			); 			wp_dropdown_categories($type_args); 		?>		<input type="submit" value="<?php echo $search_params_tour['submit']; ?>" id="searchform-tour" class="button" />	</div></form>